import React from 'react';
import { Container, Row, Col } from 'reactstrap';
import { useParams } from 'react-router';
import tshirtList from '../../../shared/shirts';
import classes from './TShirtPage.module.css';

function addColorButton(colors) {

    var but = document.querySelector(".button")
    // alert(but)
    for(var i = 0; i < colors["colors"].length; i++) {
        alert("inside create button")
        var colorButton = document.createElement('button');
        but.appendChild(colorButton)
        // colorButton.textContent = Object.keys(but["colors"])[i];

    }

}

const TShirtPage = () =>{

    const { name } = useParams();
    const Tshirt= tshirtList.filter((shirt) => shirt.name === name)[0];
    const { description, price, colors } = Tshirt;
    addColorButton(colors)

    return (
        <Container>
            <h1>{name}</h1>
            <Row className={classes.row}>
                <Col>
                    <img style={{ width: '50%' }} src={colors.white.front} alt={name} />
                </Col>


                <Col>
                <h2>{price}</h2>
                <div>{description}</div>
                <div>
                    <div>Side: </div>
                    <button>Front</button>
                    <button>Back</button>
                </div>

                <div>
                    <div>Color: </div>
                </div>
                <div className="button"></div>




                </Col>
            </Row>
        </Container>

    )

}
export default TShirtPage;
