import React, { useState, useRef} from 'react';
import CartItem from './CartItem';

const CartPage = (props) =>{
   console.log(props)
   console.log(props.deleteItem)
   console.log(props.itemsList)
   var list = props.itemsList

   const itemComponents = list.map((item) => {
    return (
        <CartItem 
         name={item.shirt_image}
         count={item.shirt_name}
         price={item.shirt_price}
         size={item.shirt_size}
        />
    );
    });

   function loadItem() {
    var list = props.itemsList
    for(var i = 0; i < list.length; i++) {
        console.log(list[i])

    }
   }


    return(
        <Container>
            <div>
                <h1>My Cart</h1>
                {/* <h1>({})</h1> */}
            </div>
            <Col>
              {itemComponents}
            </Col>
            <Col>
            <div>
                <h2>Order Summary</h2>
                <div>
                    <div>
                        <div>Subtotal:</div>
                        <div></div>
                    </div>

                    <div>
                        <div>Est.Shipping:</div>
                        <div></div>
                    </div>

                    <div>
                        <div>Total:</div>
                        <div></div>
                    </div>
                </div>
                <div>
                    <button>Log in and Checkout</button>
                </div>
            </div>
            <div>
                <button>Continue Shopping</button>
            </div>

            </Col>

        </Container>

    )

}

export default CartPage;
