    <div className="App">


      <BrowserRouter>
      <NavBar />
      <div className='MainContent'>
        <Routes>
          <Route exact path={appRoutes.home} element={<HomePage/>}></Route>
          <Route exact path={appRoutes.tshirts} element={<TShirtsPage/>}></Route>
          <Route exact path={appRoutes.tshirt} element={<TShirtPage/>}></Route>
          <Route exact path={appRoutes.cart} element={<CartPage/>}></Route>
        </Routes>
      </div>
      <Footer />
      </BrowserRouter>











    </div>
  );
}

export default App;
