import { Container, Row, Col } from 'reactstrap';
import React from 'react';
import CartItem from './CartItem';
import classes from './CartPage.module.css'

const CartPage = (props) =>{
   console.log(props)
   console.log(props.deleteItem)
   console.log(props.itemsList)
   var list = props.itemsList

   const itemComponents = list.map((item) => {
    return (
        <CartItem 
         shirt_image={item.s.shirt_image}
         shirt_name={item.s.shirt_name}
         shirt_counts={item.s.shirt_counts}
         shirt_price={item.s.shirt_price}
         shirt_size={item.s.shirt_size}
         shirt_color={item.s.shirt_color}
        />
    );
    });

    return(
        <Container>
            <div>
                <h1>My Cart({list.length})</h1>
            </div>
            <Row>
                <Col className={classes.row}>
                    <div>{itemComponents}</div>
                </Col>

                <Col>
                    <div>
                    <h2>Order Summary</h2>
                    <div>

                    <div>
                        <div>Subtotal:</div>
                        <div></div>
                    </div>

                    <div>
                        <div>Est.Shipping:</div>
                        <div></div>
                    </div>

                    <div>
                        <div>Total:</div>
                        <div></div>
                    </div>
                </div>
                <div>
                    <button>Log in and Checkout</button>
                </div>
            </div>
            <div>
                <button>Continue Shopping</button>
            </div>
            </Col>
            </Row>
        </Container>

    )

}

export default CartPage;
